/* 整个搜藏界面 */
.collection-m {
    width: 920px;
    border-radius: 4px;
    background: #fff;
    overflow: hidden;
}

/* 搜藏列表内容 上级*/
.collection-m .content {
    padding: 0px 20px;
    overflow: auto;
    height: 350px;
}

/* 搜藏列表li */
.collection-m .content .group-list li {
    padding-bottom: 24px;
    font-size: 14px;
    color: #222;
    cursor: pointer;
    float: left;
    width: 198px;
    border-right: 1px dotted #cccccc80;
    padding: 3px 9px;
    border-bottom: 1px dotted #cccccc40;
    margin: 1px;
}
/* 鼠标放上去的颜色 */
.collection-m .content .group-list li:hover {
    color: #00a1d6;
    border-bottom: 1px solid #00F;
}


/*搜藏列表中小方块和右侧文字的间距*/
.collection-m .content .group-list li input[type=checkbox]+i {
    margin-right: 7px !important; 
}

/* 新建搜藏夹 按钮*/
.collection-m .bottom {
    height: 76px;
    text-align: center;
    margin: 0 36px;
    border-top: 1px solid #e5e9ef;
}

/* 标签背景色 S **/
.group-list li:nth-child(n) {
    background: rgba(245, 239, 10, 0.08);
}
.group-list li:nth-child(2n+1) {
    background: rgba(5, 158, 158, 0.09);
}
.group-list li:nth-child(3n+1) {
    background: rgba(34, 255, 255, 0.14);
}
.group-list li:nth-child(5n+1) {
    background: rgba(153, 170, 0, 0.2);
}
.group-list li:nth-child(4n+1) {
    background: rgba(153, 170, 0, 0.22);
}
.group-list li:nth-child(8n+1) {
    background: rgba(35, 95, 249, 0.04);
}
.group-list li:nth-child(5n) {
    background: rgba(255, 152, 0, 0.14);
}
.group-list li:nth-child(3n) {
    background: rgba(111, 125, 199, 0.08);
}
/* 标签背景色 E **/


/* 新加一个按钮位置 */
.add-group {
    margin-top: 136px;
    position: absolute;
}

.bpui-slider-progress {
    height: 6px !important;
    background: 
url("data:image/png;base64,") repeat-x !important;
}
.bilibili-player-video-progress-buffer-range {
    height: 4px !important;
    border-radius: 3px !important;
    margin-top: 1px !important;
    background: url("data:image/gif;base64,") !important;
}
.bilibili-player-video-btn-send {
    background-color: #d14836;
    border: mistyrose;
}
@keyframes hc1{
0%   {transform:scale(1.1)rotate(0);color: green;box-shadow: 0 0 15px pink}
50%  {transform:scale(1.1)rotate(180deg);color: red;box-shadow: 0 0 16px pink}
100%  {transform:scale(1.1)rotate(360deg);color: blue;box-shadow: 0 0 15px pink}
}
@keyframes hc2{
0%   {transform:scale(1.3)rotate(0);color: blue;box-shadow: 0 0 17px linen}
50%  {transform:scale(1.5)rotate(180deg);color: red;box-shadow: 0 0 0 linen}
100%  {transform:scale(1.3)rotate(360deg);color: green;box-shadow: 0 0 17px linen}
}
.bpui-slider-handle{
    border-radius: 2px !important;
    font-family: 黑体 !important;
    font-size : 13px !important;
    line-height: 0.71;
    animation:hc1 0.3s;
    -moz-animation:hc1 0.3s; /* Firefox */
    -webkit-animation:hc1 0.3s; /* Safari and Chrome */
    animation-iteration-count: infinite;
    animation-timing-function: linear;
    /*background: url(data:image/gif;base64,R0lGODlhCwAKAJEDAPyZCveDBuJmBP///yH5BAEAAAMALAAAAAALAAoAAAIdhD1zopgTXgMpsBdylVCPK2UCKI0j95hoRa0NVwAAOw==);*/
}
.bpui-slider-handle:hover{
    border-radius: 13px 3px !important;
    animation:hc2 0.08s;
    -moz-animation:hc2 0.08s; /* Firefox */
    -webkit-animation:hc2 0.08s; /* Safari and Chrome */
    animation-iteration-count: infinite;
    animation-timing-function: linear;
    /*background: url(data:image/gif;base64,R0lGODlhCwAKAJEDAPyZCveDBuJmBP///yH5BAEAAAMALAAAAAALAAoAAAIdhD1zopgTXgMpsBdylVCPK2UCKI0j95hoRa0NVwAAOw==);*/
}

